/* * modprog1, 2018a * Ex #2a a program that identifies if numbers are odd or even positive or * negative or zero * Written by: qussay fiorn * id: 327601779 * login: kuchipe * * usage example * input: 1 -2 3 0 * output: * 2 2 * 2 1 1 * * output is ordered as so * evens odds * positives zeros negatives */#include <iostream>using std::cout;using std::cin;using std::endl;int main(){  //  int num1, num2, num3, num4, //input numbers      odd = 0, even = 0, positive = 0, negative = 0, zero = 0;// counters  cin >> num1 >> num2 >> num3 >> num4; // taking values from user  if (num1 % 2 == 0) // if num1 is even add 1 to even if not add 1 to odd    even++;  else    odd++;  if (num2 % 2 == 0) // if num2 is even add 1 to even if not add 1 to odd    even++;  else    odd++;  if (num3 % 2 == 0) // if num3 is even add 1 to even if not add 1 to odd    even++;  else    odd++;  if (num4 % 2 == 0) // if num4 is even add 1 to even if not add 1 to odd    even++;  else    odd++;  if (num1 > 0) // if num1 is great than zero add 1 to positive if not add 1 to      //negative if not positive or negative add 1 to zero    positive++;  else if (num1 < 0)    negative++;  else    zero++;  if (num2 > 0)// if num2 is great than zero add 1 to positive if not add 1 to      //negative if not positive or negative add 1 to zero    positive++;  else if (num2 < 0)    negative++;  else    zero++;  if (num3 > 0)// if num3 is great than zero add 1 to positive if not add 1 to      //negative if not positive or negative add 1 to zero    positive++;  else if (num3 < 0)    negative++;  else    zero++;  if (num4 > 0)// if num4 is great than zero add 1 to positive if not add 1 to      //negative if not positive or negative add 1 to zero    positive++;  else if (num4 < 0)    negative++;  else    zero++;  //Displaying results  cout << even << " " << odd << endl;  cout << positive << " " << zero  << " " << negative << endl;}